import{e}from"./app.299d7c62.js";import{_ as a}from"./plugin-vue_export-helper.5a098b48.js";const r={},t=e('<h1 id="\u91CD\u8981\u6982\u5FF5" tabindex="-1"><a class="header-anchor" href="#\u91CD\u8981\u6982\u5FF5" aria-hidden="true">#</a> \u91CD\u8981\u6982\u5FF5</h1><h2 id="cluster" tabindex="-1"><a class="header-anchor" href="#cluster" aria-hidden="true">#</a> Cluster</h2><p>Cluster \u662F\u8BA1\u7B97\u3001\u5B58\u50A8\u548C\u7F51\u7EDC\u8D44\u6E90\u7684\u96C6\u5408\uFF0CKubernetes \u5141\u8BB8\u4EE5 \u201C\u62BD\u8C61\u7684\u5F62\u5F0F\u201D \u5C06\u5BB9\u5668\u5316\u7684\u5E94\u7528\u90E8\u7F72\u5230\u96C6\u7FA4\uFF0C\u800C\u65E0\u9700\u5C06\u5B83\u4EEC\u6307\u5B9A\u5230\u67D0\u4E2A\u7279\u5B9A\u7684\u72EC\u7ACB\u7684\u8BA1\u7B97\u673A\u3002</p><p><img src="https://d33wubrfki0l68.cloudfront.net/283cc20bb49089cb2ca54d51b4ac27720c1a7902/34424/docs/tutorials/kubernetes-basics/public/images/module_01_cluster.svg" alt="img"></p><h2 id="master" tabindex="-1"><a class="header-anchor" href="#master" aria-hidden="true">#</a> Master</h2><p>Master \u7684\u804C\u8D23\u662F\u8C03\u5EA6\u6574\u4E2A Cluster\u3002Master \u534F\u8C03 Cluster \u4E2D\u7684\u6240\u6709\u6D3B\u52A8\uFF0C\u4F8B\u5982\u8C03\u5EA6\u5E94\u7528\u3001\u7EF4\u62A4\u5E94\u7528\u7684\u6240\u9700\u72B6\u6001\u3001\u5E94\u7528\u6269\u5BB9\u4EE5\u53CA\u63A8\u51FA\u65B0\u7684\u66F4\u65B0\u3002</p><h2 id="node" tabindex="-1"><a class="header-anchor" href="#node" aria-hidden="true">#</a> Node</h2><p>Node \u7684\u804C\u8D23\u662F\u8FD0\u884C\u5BB9\u5668\u5316\u7684\u5E94\u7528\u3002Node \u662F\u4E00\u4E2A\u7269\u7406\u673A\u6216 VM\uFF0C\u6BCF\u4E2A Node \u90FD\u6709 Kubelet\uFF0C\u5B83\u7BA1\u7406 Node \u5E76\u4E14\u662F Node \u4E0E Master \u901A\u4FE1\u7684\u4EE3\u7406\u3002</p><h2 id="pod" tabindex="-1"><a class="header-anchor" href="#pod" aria-hidden="true">#</a> Pod</h2><p><img src="https://d33wubrfki0l68.cloudfront.net/5cb72d407cbe2755e581b6de757e0d81760d5b86/a9df9/docs/tutorials/kubernetes-basics/public/images/module_03_nodes.svg" alt="img"></p><p>Pod \u662F Kubernetes \u7684\u6700\u5C0F\u5DE5\u4F5C\u5355\u5143\u3002\u6BCF\u4E2A Pod \u5305\u542B\u4E00\u4E2A\u6216\u591A\u4E2A\u5BB9\u5668\u3002Pod \u4E2D\u7684\u5BB9\u5668\u4F1A\u4F5C\u4E3A\u4E00\u4E2A\u6574\u4F53\u88AB Master \u8C03\u5EA6\u5230\u4E00\u4E2A Node \u4E0A\u8FD0\u884C\u3002</p><p>Kubernetes \u5F15\u5165 Pod \u4E3B\u8981\u57FA\u4E8E\u4E0B\u9762\u4E24\u4E2A\u76EE\u7684\uFF1A</p><ol><li><p>\u53EF\u7BA1\u7406\u6027\uFF1A</p><p>\u6709\u4E9B\u5BB9\u5668\u5929\u751F\u5C31\u662F\u9700\u8981\u7D27\u5BC6\u8054\u7CFB\uFF0C\u4E00\u8D77\u5DE5\u4F5C\u3002Pod \u63D0\u4F9B\u4E86\u6BD4\u5BB9\u5668\u66F4\u9AD8\u5C42\u6B21\u7684\u62BD\u8C61\uFF0C\u5C06\u5B83\u4EEC\u5C01\u88C5\u5230\u4E00\u4E2A\u90E8\u7F72\u5355\u5143\u4E2D\u3002Kubernetes \u4EE5 Pod \u4E3A\u6700\u5C0F\u5355\u4F4D\u8FDB\u884C\u8C03\u5EA6\u3001\u6269\u5C55\u3001\u5171\u4EAB\u8D44\u6E90\u3001\u7BA1\u7406\u751F\u547D\u5468\u671F\u3002</p></li><li><p>\u901A\u4FE1\u548C\u8D44\u6E90\u5171\u4EAB\uFF1A</p><p>Pod \u4E2D\u7684\u6240\u6709\u5BB9\u5668\u4F7F\u7528\u540C\u4E00\u4E2A\u7F51\u7EDC namespace\uFF0C\u5373\u76F8\u540C\u7684 IP \u5730\u5740\u548C Port \u7A7A\u95F4\u3002\u5B83\u4EEC\u53EF\u4EE5\u76F4\u63A5\u7528 localhost \u901A\u4FE1\u3002\u540C\u6837\u7684\uFF0C\u8FD9\u4E9B\u5BB9\u5668\u53EF\u4EE5\u5171\u4EAB\u5B58\u50A8\uFF0C\u5F53 Kubernetes \u6302\u5728 volume \u5230 Pod\uFF0C\u672C\u8D28\u4E0A\u662F\u5C06 volume \u6302\u8F7D\u5230 Pod \u4E2D\u7684\u6BCF\u4E00\u4E2A\u5BB9\u5668\u3002</p></li></ol><p>Pods \u6709\u4E24\u79CD\u4F7F\u7528\u65B9\u5F0F\uFF1A</p><ol><li>\u8FD0\u884C\u5355\u4E00\u5BB9\u5668</li><li>\u8FD0\u884C\u591A\u4E2A\u5BB9\u5668</li></ol><h2 id="controller" tabindex="-1"><a class="header-anchor" href="#controller" aria-hidden="true">#</a> Controller</h2><p>Kubernetes \u901A\u5E38\u4E0D\u4F1A\u76F4\u63A5\u521B\u5EFA Pod\uFF0C\u800C\u662F\u901A\u8FC7 Controller \u6765\u7BA1\u7406 Pod \u7684\u3002Controller \u4E2D\u5B9A\u4E49\u4E86 Pod \u7684\u90E8\u7F72\u7279\u6027\uFF0C\u6BD4\u5982\u6709\u51E0\u4E2A\u526F\u672C\uFF0C\u5728\u4EC0\u4E48\u6837\u7684 Node \u4E0A\u8FD0\u884C\u7B49\u3002\u4E3A\u4E86\u6EE1\u8DB3\u4E0D\u540C\u7684\u4E1A\u52A1\u573A\u666F\uFF0CKubernetes \u63D0\u4F9B\u4E86\u591A\u79CD Controller\uFF0C\u5305\u62EC Deployment\u3001ReplicaSet\u3001DaemonSet\u3001StatefulSet\u3001Job \u7B49\u3002</p><h3 id="deployment" tabindex="-1"><a class="header-anchor" href="#deployment" aria-hidden="true">#</a> Deployment</h3><p>Deployment \u662F\u6700\u5E38\u7528\u7684 Controller\uFF0CDeployment \u53EF\u4EE5\u7BA1\u7406 Pod \u7684\u591A\u4E2A\u526F\u672C\uFF0C\u5E76\u786E\u4FDD Pod \u6309\u7167\u671F\u671B\u7684\u72B6\u6001\u8FD0\u884C\u3002</p><h3 id="replicaset" tabindex="-1"><a class="header-anchor" href="#replicaset" aria-hidden="true">#</a> ReplicaSet</h3><p>ReplicaSet \u5B9E\u73B0\u4E86 Pod \u7684\u591A\u526F\u672C\u7BA1\u7406\u3002\u4F7F\u7528 Deployment \u65F6\u81EA\u52A8\u521B\u5EFA ReplicaSet\uFF0C\u4E5F\u5C31\u662F\u8BF4 Deployment \u662F\u901A\u8FC7 ReplicaSet \u6765\u7BA1\u7406 Pod \u7684\u591A\u4E2A\u526F\u672C\u7684\uFF0C\u6211\u4EEC\u901A\u5E38\u4E0D\u9700\u8981\u76F4\u63A5\u4F7F\u7528 ReplicaSet\u3002</p><h3 id="daemonset" tabindex="-1"><a class="header-anchor" href="#daemonset" aria-hidden="true">#</a> DaemonSet</h3><p>DaemonSet \u7528\u4E8E\u6BCF\u4E2A Node \u6700\u591A\u53EA\u8FD0\u884C\u4E00\u4E2A Pod \u7684\u573A\u666F\uFF0C\u800C\u5176\u901A\u5E38\u7528\u4E8E\u8FD0\u884C daemon\u3002</p><h3 id="statefulset" tabindex="-1"><a class="header-anchor" href="#statefulset" aria-hidden="true">#</a> StatefulSet</h3><p>StatefulSet \u80FD\u591F\u4FDD\u8BC1 Pod \u7684\u6BCF\u4E2A\u526F\u672C\u5728\u6574\u4E2A\u751F\u547D\u5468\u671F\u4E2D\u7684\u540D\u79F0\u662F\u4E0D\u53D8\u7684\uFF0C\u800C\u5176\u4ED6 Controller \u4E0D\u63D0\u4F9B\u8FD9\u4E2A\u529F\u80FD\u3002\u5F53\u67D0\u4E2A Pod \u53D1\u751F\u6545\u969C\u9700\u8981\u5220\u9664\u5E76\u91CD\u65B0\u542F\u52A8\u65F6\uFF0CPod \u7684\u540D\u79F0\u4F1A\u53D1\u751F\u53D8\u5316\uFF0C\u540C\u65F6 StatefulSet \u4F1A\u4FDD\u8BC1\u526F\u672C\u6309\u7167\u56FA\u5B9A\u7684\u987A\u5E8F\u542F\u52A8\u3001\u66F4\u65B0\u6216\u5220\u9664\u3002</p><h3 id="job" tabindex="-1"><a class="header-anchor" href="#job" aria-hidden="true">#</a> Job</h3><p>Job \u7528\u4E8E\u8FD0\u884C\u7ED3\u675F\u5C31\u5220\u9664\u7684\u5E94\u7528\u3002</p><h2 id="service" tabindex="-1"><a class="header-anchor" href="#service" aria-hidden="true">#</a> Service</h2><p>Deployment \u53EF\u4EE5\u90E8\u7F72\u591A\u4E2A\u526F\u672C\uFF0C\u6BCF\u4E2A Pod \u90FD\u6709\u81EA\u5DF1\u7684 IP\uFF0C\u5916\u754C\u5982\u4F55\u8BBF\u95EE\u8FD9\u4E9B\u526F\u672C\u7684 IP \u5462\uFF1F\u8981\u77E5\u9053 Pod \u5F88\u53EF\u80FD\u4F1A\u88AB\u9891\u7E41\u7684\u9500\u6BC1\u548C\u91CD\u542F\uFF0C\u5B83\u4EEC\u7684 IP \u4E5F\u4F1A\u968F\u4E4B\u53D8\u5316\uFF0C\u7528 IP \u6765\u8BBF\u95EE\u5F88\u4E0D\u73B0\u5B9E\u3002</p><p>\u5982\u679C\u5728 Pod \u524D\u589E\u52A0\u4E00\u4E2A Service \u5219\u53EF\u4EE5\u5F88\u597D\u7684\u89E3\u51B3\u8FD9\u4E2A\u95EE\u9898\uFF0C\u53EA\u9700\u8981\u53BB\u8BBF\u95EE Service \u7684 IP \u53CA Port \u5373\u53EF\u3002</p><p>\u6545\uFF0CKubernetes \u8FD0\u884C\u5BB9\u5668\u4E0E\u8BBF\u95EE\u5BB9\u5668\u5206\u522B\u7531 Controller \u548C Service \u6267\u884C\u3002</p><h2 id="namespace" tabindex="-1"><a class="header-anchor" href="#namespace" aria-hidden="true">#</a> Namespace</h2><p>\u5982\u679C\u6709\u591A\u4E2A\u7528\u6237\u6216\u9879\u76EE\u7EC4\u4F7F\u7528\u540C\u4E00\u4E2A Kubernetes Cluster\uFF0C\u5982\u4F55\u5C06\u4ED6\u4EEC\u521B\u5EFA\u7684 Controller\u3001Pod \u7B49\u8D44\u6E90\u5206\u5F00\u5462\uFF1F</p><p>Namespace \u53EF\u4EE5\u5C06\u4E00\u4E2A\u7269\u7406\u7684 Cluuster \u903B\u8F91\u5212\u5206\u4E3A\u591A\u4E2A\u865A\u62DF\u7684 Cluster\uFF0C\u6BCF\u4E2A Cluster \u5C31\u662F\u4E00\u4E2A Namespace\u3002\u4E0D\u540C\u7684 Namespace \u91CC\u7684\u8D44\u6E90\u662F\u5B8C\u5168\u9694\u79BB\u7684\u3002</p><p>Kubernetes \u9ED8\u8BA4\u521B\u5EFA\u4E86\u4E24\u4E2A Namespace <code>default</code> \u548C <code>kube-system</code>\u3002\u521B\u5EFA\u8D44\u6E90\u65F6\u5982\u679C\u4E0D\u6307\u5B9A\uFF0C\u5C06\u88AB\u653E\u5230 <code>default</code> \u4E2D\uFF0CKubernetes \u81EA\u5DF1\u521B\u5EFA\u7684\u7CFB\u7EDF\u8D44\u6E90\u5C06\u653E\u5230 <code>kube-system</code> \u4E2D\u3002</p>',35);function d(o,s){return t}var i=a(r,[["render",d]]);export{i as default};
