import{e as a}from"./app.6fa1c169.js";import{_ as n}from"./plugin-vue_export-helper.5a098b48.js";const s={},e=a('<h2 id="介绍" tabindex="-1"><a class="header-anchor" href="#介绍" aria-hidden="true">#</a> 介绍</h2><p>初步安装系统后日期时间显示的并非当前时区。</p><h2 id="解决方案" tabindex="-1"><a class="header-anchor" href="#解决方案" aria-hidden="true">#</a> 解决方案</h2><div class="language-bash ext-sh"><pre class="language-bash"><code><span class="token comment"># 1. 显示时间</span>\n<span class="token function">date</span> -R\n<span class="token comment"># Sun, 07 Jul 2019 xx:xx:xx +0000</span>\n\n<span class="token comment"># 2. 修改时区</span>\n<span class="token function">sudo</span> tzselect\n<span class="token comment"># 按照提示修改即可</span>\n\n<span class="token comment"># 3. 拷贝文件到本地时间</span>\n<span class="token function">sudo</span> <span class="token function">cp</span> /usr/share/zoneinfo/Asia/Shanghai /etc/localtime\n<span class="token comment"># 再次执行 date -R 即可看到正确时间</span>\n</code></pre></div><h2 id="扩展" tabindex="-1"><a class="header-anchor" href="#扩展" aria-hidden="true">#</a> 扩展</h2><ul><li><p>常用的时间格式：</p><div class="language-bash ext-sh"><pre class="language-bash"><code><span class="token function">date</span> <span class="token string">&quot;+%Y-%m-%d %H:%M:%S&quot;</span>\n</code></pre></div></li></ul>',6);var t=n(s,[["render",function(a,n){return e}]]);export{t as default};
