import{r as n,o as s,c as a,a as e,F as t,b as o,d as p}from"./app.b9dc9220.js";const c={},r=e("p",null,[o("当然，你首先需要"),e("a",{href:"/os/centos/how-to-install-centos-on-virtualbox"},"安装 CentOS"),o("。")],-1),l=e("h2",{id:"更新源",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#更新源","aria-hidden":"true"},"#"),o(" 更新源")],-1),u=o("CentOS 安装完毕，首先应该修改源，并更新到最新的系统。此处选用阿里云的源，"),i={href:"https://mirrors.tuna.tsinghua.edu.cn/",target:"_blank",rel:"noopener noreferrer"},d=o("清华源"),h=o("，"),m={href:"http://mirrors.ustc.edu.cn/",target:"_blank",rel:"noopener noreferrer"},k=o("中科大源"),f=o("也可。"),g=p('<div class="language-bash ext-sh"><pre class="language-bash"><code><span class="token comment"># 安装工具 wget 或者 curl</span>\nyum <span class="token function">install</span> <span class="token function">wget</span> -y\n\n<span class="token comment"># 备份源</span>\n<span class="token function">mv</span> /etc/yum.repos.d/CentOS-Base.repo /etc/yum.repos.d/CentOS-Base.repo.bak\n\n<span class="token comment"># 下载阿里云源</span>\n<span class="token function">curl</span> -o /etc/yum.repos.d/CentOS-Base.repo https://mirrors.aliyun.com/repo/Centos-7.repo\n<span class="token function">curl</span> -o /etc/yum.repos.d/epel.repo https://mirrors.aliyun.com/repo/epel-7.repo\n\n<span class="token comment"># 清理、生成缓存</span>\nyum clean cache\nyum makecache\n</code></pre></div><h2 id="更新系统" tabindex="-1"><a class="header-anchor" href="#更新系统" aria-hidden="true">#</a> 更新系统</h2><p>更换源之后就可以使用国内的镜像了，否则下载速度太慢。</p><div class="language-bash ext-sh"><pre class="language-bash"><code>yum update -y\n</code></pre></div><h2 id="常用包组" tabindex="-1"><a class="header-anchor" href="#常用包组" aria-hidden="true">#</a> 常用包组</h2><p>一般在安装系统时需要安装的软件包组。</p><div class="language-bash ext-sh"><pre class="language-bash"><code>yum <span class="token function">groups</span> mark convert\n\n<span class="token comment"># 查看所有包组名称</span>\nyum grouplist\n\n<span class="token comment"># 如果缺乏部分包组，指定该包组</span>\nyum groupinstall <span class="token string">&quot;Cinnamon&quot;</span> -y\n</code></pre></div><h2 id="常用工具" tabindex="-1"><a class="header-anchor" href="#常用工具" aria-hidden="true">#</a> 常用工具</h2><p>更新完系统后还需要一些常用的工具，使用 Ubuntu Server 的时候同样需要安装，只不过指令换为 <code>apt install</code>。</p><div class="language-bash ext-sh"><pre class="language-bash"><code>yum <span class="token function">install</span> tree nmap dos2unix lrzsz <span class="token function">nc</span> <span class="token function">lsof</span> <span class="token function">wget</span> tcpdump <span class="token function">htop</span> iftop iotop sysstat nethogs psmisc net-tools bash-completion vim-enhanced -y\n</code></pre></div><h2 id="常用指令" tabindex="-1"><a class="header-anchor" href="#常用指令" aria-hidden="true">#</a> 常用指令</h2><div class="language-bash ext-sh"><pre class="language-bash"><code><span class="token comment"># 查看 ip</span>\n<span class="token function">ip</span> addr\n<span class="token comment"># 查看服务的端口（例如：ssh）</span>\nss -lntup <span class="token operator">|</span> <span class="token function">grep</span> sshd <span class="token comment"># 或 netstat -lntup</span>\n<span class="token comment"># 查看进程（例如：ssh）</span>\n<span class="token function">ps</span> -ef <span class="token operator">|</span> <span class="token function">grep</span> <span class="token function">ssh</span>\n<span class="token comment"># 查看是否安装了对应的软件</span>\n<span class="token function">rpm</span> -qa openssh openssl\n<span class="token comment"># openssh 提供 SSH 服务，openssl 为 SSH 提供连接加密</span>\n</code></pre></div>',12);c.render=function(o,p){const c=n("OutboundLink");return s(),a(t,null,[r,l,e("p",null,[u,e("a",i,[d,e(c)]),h,e("a",m,[k,e(c)]),f]),g],64)};export default c;
